import React, { useEffect, useState } from "react";
import "./Dashboard.css";
import Button from "react-bootstrap/Button";
import { useNavigate } from "react-router-dom";
import Modal from "react-bootstrap/Modal";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import { userLogout, userUpdate } from "../services/auth.service";
import { useFormik } from "formik";
import { PopupValidations } from "../form-validations/FormValidations";

const Dashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const initial = {
    _id: user._id,
    first_name: user.first_name,
    last_name: user.last_name,
    add_line1: user.address?.add_line1,
    add_line2: user.address?.add_line2,
    state: user.address?.state,
    city: user.address?.city,
    mobile: user.mobile,
    email: user.email,
  };

  const [show, setShow] = useState(false);
  const { values, errors, touched, handleChange, handleSubmit } = useFormik({
    initialValues: initial,
    validationSchema: PopupValidations,
    onSubmit: async (values) => {
      const updatedResponse = await userUpdate(values);
      if (updatedResponse.data.status) {
        handleClose();
      }
    },
  });

  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  const handleLogout = () => {
    userLogout();
    navigate("/");
  };

  return (
    <>
      <div className="text-right mt-3 mr-3">
        <Button variant="primary" onClick={handleShow}>
          Update Profile
        </Button>{" "}
        <Button variant="danger" onClick={() => handleLogout()}>
          Logout
        </Button>{" "}
      </div>
      <br />
      <br />
      <br />
      <br />

      <div className="card mt-5">
        <div className="box">
          <div className="img">
            <img src="data:image/jpeg;base64," />
          </div>
          <h2>
            <b>
              Welcome, {user.first_name} {user.last_name}
              <br />
              <span>&nbsp;Angular Fullstack Developer</span>
            </b>
          </h2>
          <p>
            <i>
              <b>
                Hello, I am {user.first_name} {user.last_name}, working as react
                fullstack developer and angular fullstack developer in smartData
                Nagpur.
              </b>
            </i>
          </p>
          <span>
            <ul>
              <li>
                <a href="#">
                  <i className="fa fa-facebook" aria-hidden="true"></i>
                </a>
              </li>
              <li>
                <a href="#">
                  <i className="fa fa-twitter" aria-hidden="true"></i>
                </a>
              </li>
              <li>
                <a href="#">
                  <i className="fa fa-google-plus" aria-hidden="true"></i>
                </a>
              </li>
              <li>
                <a href="#">
                  <i className="fa fa-linkedin" aria-hidden="true"></i>
                </a>
              </li>
              <li>
                <a href="#">
                  <i className="fa fa-instagram" aria-hidden="true"></i>
                </a>
              </li>
            </ul>
          </span>
        </div>
      </div>

      {/* -------------------------Popup------------------------------------- */}
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Update Profile</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Row className="mb-3">
              <Form.Group as={Col} controlId="formGridEmail">
                <Form.Label>First Name</Form.Label>
                <Form.Control
                  type="text"
                  value={values?.first_name}
                  name="first_name"
                  onChange={handleChange}
                />
                {errors.first_name && touched.first_name ? (
                  <p className="text-danger">{errors.first_name}</p>
                ) : null}
                <Form.Label>Address Line1</Form.Label>
                <Form.Control
                  type="text"
                  value={values?.add_line1}
                  name="add_line1"
                  onChange={handleChange}
                />
                {errors.add_line1 && touched.add_line1 ? (
                  <p className="text-danger">{errors.add_line1}</p>
                ) : null}
                <Form.Label>State</Form.Label>
                <Form.Control
                  type="text"
                  value={values?.state}
                  name="state"
                  onChange={handleChange}
                />
                {errors.state && touched.state ? (
                  <p className="text-danger">{errors.state}</p>
                ) : null}
                <Form.Label>Mobile</Form.Label>
                <Form.Control
                  type="number"
                  value={values?.mobile}
                  name="mobile"
                  onChange={handleChange}
                />
                {errors.mobile && touched.mobile ? (
                  <p className="text-danger">{errors.mobile}</p>
                ) : null}
              </Form.Group>

              <Form.Group as={Col} controlId="formGridPassword">
                <Form.Label>Last Name</Form.Label>
                <Form.Control
                  type="text"
                  value={values?.last_name}
                  name="last_name"
                  onChange={handleChange}
                />
                {errors.last_name && touched.last_name ? (
                  <p className="text-danger">{errors.last_name}</p>
                ) : null}
                <Form.Label>Address Line2 </Form.Label>
                <Form.Control
                  type="text"
                  value={values?.add_line2}
                  name="add_line2"
                  onChange={handleChange}
                />
                <Form.Label>City</Form.Label>
                <Form.Control
                  type="text"
                  value={values?.city}
                  name="city"
                  onChange={handleChange}
                />
                {errors.city && touched.city ? (
                  <p className="text-danger">{errors.city}</p>
                ) : null}
                <Form.Label>Email </Form.Label>
                <Form.Control type="text" value={values?.email} readOnly />
              </Form.Group>
            </Row>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            Update
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default Dashboard;
